import React from 'react';
import { styled } from 'styled-components';
import { useContext } from 'react';
import { MyContext } from '../context/mycontext';
import CardSmall from './card_small';
import Button from './button';


const StyledCheckout = styled.section`
  width: 100%;
  height: auto;

  > div:first-child{
    display: flex;
    flex-wrap: wrap;

    article {
      width: 50%;
      height: auto;
    }

    .article_first_child {
      display: flex;
      align-items: center;
      flex-direction: column;
      justify-content: center;
      border-right: 1px solid gray;
      padding: 100px 50px;

      > div:first-child {
        height: 100px;

        > h1 {
          font-size: 30px;
        }
      }
    }

    .article_second_child {
      > div {
        width: 100%;
        display: flex;
        align-items: center;
        flex-direction: column;
        padding: 100px 50px;

        > div:first-child {
          height: 100px;

          h1 {
            font-size: 30px;
          }
        }

        > div:nth-child(2) {
          display: flex;
          width: 100%;
          flex-direction: column;

          div {
            display: flex;
            gap: 20px;
            flex-direction: column;

            input {
              max-width: 350px;
              border: none;
              height: 40px;
              border-radius: 10px;
              margin-bottom:20px;
              background-color: #efefef;
            }

            label {
              font-size: 15px;
            }
          }
        }
      }
    }
  }

  .container_button{
    width:100%;
    height:200px;
    display: flex;
    align-items:center;
    justify-content: center;
  }
`
const Checkout = () => {

    const {productShopping, setProductShopping} = useContext(MyContext);


  return (
    <StyledCheckout>
        <div>
            <article className='article_first_child'>
                <div>
                    <h1>Mi carrito</h1>
                </div>
                <div>
                    {
                        productShopping.map((product) =>
                        <CardSmall
                        name={product.nombre}
                        price={product.precio}
                        cantidad={product.cantidad }
                        style={{width: "400px", height: "150px", fontSize:"30px"}}
                        hidden={false}
                        />
                        )
                    }
                </div>
            </article>
            <article className='article_second_child'>   
                <div>  
                    <div className='div_first'>
                        <h1>Información personal</h1>
                    </div>
                    <div>
                        <div>
                            <label htmlFor="">Nombre y apellidos</label>
                            <input type="text" />
                        </div>
                        <div>
                            <label htmlFor="">Documento identidad</label>
                            <input type="text" />
                        </div>
                        <div>
                            <label htmlFor="">Dirección</label>
                            <input type="text" />
                        </div>
                        <div>
                            <label htmlFor="">Barrio</label>
                            <input type="text" />
                        </div>
                        <div>
                            <label htmlFor="">Ciudad</label>
                            <input type="text" />
                        </div>
                        <div>
                            <label htmlFor="">Email</label>
                            <input type="text" />
                        </div>
                    </div>
                    <div className='container_button'>
                        <Button
                        text={"Finalizar compra"}
                        />
                    </div>
                </div>
            </article>
        </div>
    </StyledCheckout>
  )
}

export default Checkout
