import { createContext, useState } from "react";
import { useEffect } from "react";

export const MyContext = createContext();


const MyProvider = ({children}) => {

  const [products, setProducts] = useState(() => {
    const savedProducts = localStorage.getItem('products');
    return savedProducts ? JSON.parse(savedProducts) : [
      {id: 0, nombre: "Camisa", precio: 50000, cantidad: 45},
    ];
  });

  useEffect(() => {
    localStorage.setItem('products', JSON.stringify(products));
  }, [products]);



  const [productShopping, setProductShopping] = useState(() => {
    const savedproductShopping = localStorage.getItem('productShopping');
    return savedproductShopping ? JSON.parse(savedproductShopping) : [
      {id: 0, nombre: "Camisa", precio: 50000, cantidad: 45},
    ];
  });

  useEffect(() => {
    localStorage.setItem('productShopping', JSON.stringify(productShopping));
  }, [productShopping]);


  const [purchaseSuccessful, setPurchaseSuccessful] = useState(false)



  const [shopping, setShopping] = useState(false)

  return (
    <MyContext.Provider value={{products, setProducts, shopping, setShopping, productShopping, setProductShopping, purchaseSuccessful, setPurchaseSuccessful}}>
        {children}
    </MyContext.Provider>
  )
}

export default MyProvider
